CREATE TABLE pages (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	parent_id INTEGER NOT NULL,	alias TEXT NOT NULL,	path TEXT NOT NULL default '',	position TEXT NOT NULL default '',	status INTEGER NOT NULL default '1',	layout TEXT NOT NULL,	type TEXT NOT NULL,	cdate INTEGER NOT NULL);CREATE TABLE pages_content (	page_id INTEGER NOT NULL,	locale TEXT NOT NULL,	title TEXT default NULL,	content TEXT,	UNIQUE (page_id,locale));CREATE TABLE pages_meta (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	page_id INTEGER NOT NULL,	key TEXT,	value TEXT,	UNIQUE (page_id,key));CREATE TABLE config (	id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,	module TEXT,	key TEXT,	value TEXT,	UNIQUE (module,key));CREATE TABLE routes (	alias TEXT NOT NULL,	route TEXT NOT NULL,	params TEXT,	UNIQUE (alias,route));INSERT INTO pages VALUES ('0','0','','','','0','','',0);INSERT INTO pages_meta (page_id, key, value) VALUES (0, 'meta_title', '{title}');INSERT INTO pages_meta (page_id, key, value) VALUES (0, 'h1', '{title}');INSERT INTO routes (alias, route) VALUES ('/robots.txt', 'common/robots');INSERT INTO routes (alias, route) VALUES ('/sitemap.xml', 'common/sitemap');